import React, { useState, useEffect } from 'react';
import Register from './Register';

function App() {
  const [user, setUser] = useState(null);
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [errorLogin, setErrorLogin] = useState('');

  const fetchCards = () => {
    setLoading(true);
    fetch('http://localhost:3002/cards')
      .then(res => res.json())
      .then(data => {
        setCards(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  };

  // Простой логин
  const login = async (login, password) => {
    setErrorLogin('');
    try {
      const res = await fetch('http://localhost:3002/login', {  // Сделаем простой роут /login
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ login, password }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Ошибка входа');
      setUser(data.user);
      fetchCards();
    } catch (e) {
      setErrorLogin(e.message);
    }
  };

  const logout = () => {
    setUser(null);
    setCards([]);
  };

  if (!user) {
    if (showRegister) {
      return (
        <div>
          <Register onRegister={(user) => {
            setUser(user);
            fetchCards();
          }} />
          <button onClick={() => setShowRegister(false)}>Войти</button>
        </div>
      );
    }
    return (
      <div>
        <h2>Вход</h2>
        {errorLogin && <p style={{ color: 'red' }}>{errorLogin}</p>}
        <LoginForm onLogin={login} />
        <button onClick={() => setShowRegister(true)}>Зарегистрироваться</button>
      </div>
    );
  }

  return (
    <div>
      <h1>Добро пожаловать, {user.login}!</h1>
      <button onClick={logout}>Выйти</button>

      <h2>Карточки книг</h2>
      {loading ? <p>Загрузка...</p> : cards.length === 0 ? <p>Карточек нет :(</p> : (
        <ul>
          {cards.map(c => (
            <li key={c.id}>
              <b>{c.title}</b> — {c.author} ({c.type})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

// Очень простой LoginForm
function LoginForm({ onLogin }) {
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    onLogin(login, password);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input placeholder="Логин" value={login} onChange={e => setLogin(e.target.value)} required />
      <input type="password" placeholder="Пароль" value={password} onChange={e => setPassword(e.target.value)} required />
      <button type="submit">Войти</button>
    </form>
  );
}

export default App;
