import React, { useState, useEffect } from 'react';

function App() {
  const [user, setUser] = useState(null); // залогиненый пользователь
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [mode, setMode] = useState('login'); // 'login' или 'register'

  // Загрузка карточек
  const fetchCards = () => {
    setLoading(true);
    fetch('http://localhost:3002/cards')
      .then(res => res.json())
      .then(data => {
        setCards(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  };

  // Регистрация
  const register = async (form) => {
    try {
      const res = await fetch('http://localhost:3002/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Ошибка регистрации');
      alert('Регистрация успешна! Можешь войти.');
      setMode('login');
    } catch (e) {
      alert(e.message);
    }
  };

  // Логин
  const login = async (loginData) => {
    try {
      const res = await fetch('http://localhost:3002/login', { // предполагаю, что у тебя есть роут login
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginData),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Ошибка входа');
      setUser(data.user); // предполагаем, что сервер возвращает пользователя
      fetchCards();
    } catch (e) {
      alert(e.message);
    }
  };

  // Логаут
  const logout = () => {
    setUser(null);
    setCards([]);
  };

  // Форма для регистрации и входа
  const AuthForm = () => {
    const [form, setForm] = useState({
      fullName: '',
      phone: '',
      email: '',
      login: '',
      password: '',
    });

    const handleChange = e => {
      setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = e => {
      e.preventDefault();
      if (mode === 'register') {
        // Проверка на пустые поля
        if (!form.fullName || !form.phone || !form.email || !form.login || !form.password) {
          alert('Заполни все поля!');
          return;
        }
        register(form);
      } else {
        if (!form.login || !form.password) {
          alert('Введи логин и пароль!');
          return;
        }
        login({ login: form.login, password: form.password });
      }
    };

    return (
      <form onSubmit={handleSubmit} style={{ maxWidth: 400, margin: '20px auto', display: 'grid', gap: 10 }}>
        <h2>{mode === 'register' ? 'Регистрация' : 'Вход'}</h2>

        {mode === 'register' && (
          <>
            <input name="fullName" placeholder="ФИО" value={form.fullName} onChange={handleChange} />
            <input name="phone" placeholder="Телефон" value={form.phone} onChange={handleChange} />
            <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} />
          </>
        )}

        <input name="login" placeholder="Логин" value={form.login} onChange={handleChange} />
        <input name="password" type="password" placeholder="Пароль" value={form.password} onChange={handleChange} />

        <button type="submit" style={{ padding: '10px', cursor: 'pointer' }}>
          {mode === 'register' ? 'Зарегистрироваться' : 'Войти'}
        </button>

        <button
          type="button"
          style={{ marginTop: 10, background: 'none', border: 'none', color: 'blue', cursor: 'pointer' }}
          onClick={() => setMode(mode === 'register' ? 'login' : 'register')}
        >
          {mode === 'register' ? 'Уже есть аккаунт? Войти' : 'Нет аккаунта? Зарегистрироваться'}
        </button>
      </form>
    );
  };

  if (!user) {
    return <AuthForm />;
  }

  return (
    <div style={{ maxWidth: 600, margin: '30px auto', fontFamily: 'Arial, sans-serif' }}>
      <h1>Добро пожаловать, {user.login}!</h1>
      <button onClick={logout} style={{ marginBottom: 20, padding: '8px 16px', cursor: 'pointer' }}>
        Выйти
      </button>

      <h2>Карточки книг</h2>
      {loading ? (
        <p>Загрузка...</p>
      ) : cards.length === 0 ? (
        <p>Карточек нет :(</p>
      ) : (
        <ul style={{ paddingLeft: 0 }}>
          {cards.map((c) => (
            <li key={c.id} style={{ listStyle: 'none', marginBottom: 10, padding: 10, border: '1px solid #ccc', borderRadius: 6 }}>
              <b>{c.title}</b> — {c.author} ({c.type})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
