import React, { useState } from 'react';

function App() {
  const [user, setUser] = useState(null);
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [errorLogin, setErrorLogin] = useState('');
  const [errorRegister, setErrorRegister] = useState('');

  // Состояния для регистрации
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');

  // Загрузка карточек книг с сервера
  const fetchCards = () => {
    setLoading(true);
    fetch('http://localhost:3002/cards')
      .then(res => res.json())
      .then(data => {
        setCards(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  };

  // Логин — простой POST запрос
  const handleLogin = async (e) => {
    e.preventDefault();
    setErrorLogin('');
    try {
      const res = await fetch('http://localhost:3002/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ login, password }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Ошибка входа');
      setUser(data.user);
      fetchCards();
    } catch (e) {
      setErrorLogin(e.message);
    }
  };

  // Регистрация
  const handleRegister = async (e) => {
    e.preventDefault();
    setErrorRegister('');
    try {
      const res = await fetch('http://localhost:3002/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ fullName, phone, email, login, password }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Ошибка регистрации');
      setUser(data.user);
      fetchCards();
    } catch (e) {
      setErrorRegister(e.message);
    }
  };

  // Выход из аккаунта
  const logout = () => {
    setUser(null);
    setCards([]);
    setLogin('');
    setPassword('');
  };

  // Если не залогинен
  if (!user) {
    if (showRegister) {
      return (
        <div style={{ padding: 20 }}>
          <h2>Регистрация</h2>
          {errorRegister && <p style={{ color: 'red' }}>{errorRegister}</p>}
          <form onSubmit={handleRegister}>
            <input
              placeholder="ФИО"
              value={fullName}
              onChange={(e) => setFullName(e.target.value)}
              required
              style={{ display: 'block', marginBottom: 10, padding: 8, width: 200 }}
            />
            <input
              placeholder="Телефон"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
              style={{ display: 'block', marginBottom: 10, padding: 8, width: 200 }}
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{ display: 'block', marginBottom: 10, padding: 8, width: 200 }}
            />
            <input
              placeholder="Логин"
              value={login}
              onChange={(e) => setLogin(e.target.value)}
              required
              style={{ display: 'block', marginBottom: 10, padding: 8, width: 200 }}
            />
            <input
              type="password"
              placeholder="Пароль"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{ display: 'block', marginBottom: 10, padding: 8, width: 200 }}
            />
            <button type="submit">Зарегистрироваться</button>
          </form>
          <button onClick={() => setShowRegister(false)}>Уже есть аккаунт? Войти</button>
        </div>
      );
    }

    return (
      <div style={{ padding: 20 }}>
        <h2>Вход</h2>
        {errorLogin && <p style={{ color: 'red' }}>{errorLogin}</p>}
        <form onSubmit={handleLogin}>
          <input
            placeholder="Логин"
            value={login}
            onChange={(e) => setLogin(e.target.value)}
            required
            style={{ display: 'block', marginBottom: 10, padding: 8, width: 200 }}
          />
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ display: 'block', marginBottom: 10, padding: 8, width: 200 }}
          />
          <button type="submit">Войти</button>
        </form>
        <button onClick={() => setShowRegister(true)}>Зарегистрироваться</button>
      </div>
    );
  }

  // Если залогинен — показываем карточки
  return (
    <div style={{ padding: 20 }}>
      <h1>Добро пожаловать, {user.login}!</h1>
      <button onClick={logout}>Выйти</button>

      <h2>Карточки книг</h2>
      {loading ? (
        <p>Загрузка...</p>
      ) : cards.length === 0 ? (
        <p>Карточек нет :(</p>
      ) : (
        <ul>
          {cards.map((c) => (
            <li key={c.id}>
              <b>{c.title}</b> — {c.author} ({c.type})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
