// src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route, Navigate, Link } from 'react-router-dom';
import RegisterPage from './pages/RegisterPage.jsx';
import LoginPage    from './pages/LoginPage.jsx';

function App() {
  return (
    <BrowserRouter>
      <nav style={{ padding: '10px', background: '#4CAF50' }}>
        <Link to="/register" style={{ color: '#fff', marginRight: 10 }}>Регистрация</Link>
        <Link to="/login"    style={{ color: '#fff' }}>Вход</Link>
      </nav>

      <Routes>
        {/* Редирект с корня на /register */}
        <Route path="/" element={<Navigate to="/register" replace />} />

        {/* Страницы */}
        <Route path="/register" element={<RegisterPage />} />
        <Route path="/login"    element={<LoginPage />} />

        {/* В случае неизвестного пути возвращаем на /register */}
        <Route path="*" element={<Navigate to="/register" replace />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
