import React, { useEffect, useState } from 'react';

function App() {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:3002/cards')
      .then((res) => {
        if (!res.ok) {
          throw new Error('Ошибка при загрузке карточек');
        }
        return res.json();
      })
      .then((data) => {
        setCards(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Загрузка карточек...</p>;
  if (error) return <p style={{ color: 'red' }}>Ошибка: {error}</p>;

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Карточки книг</h1>
      {cards.length === 0 ? (
        <p>Карточек нет :(</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {cards.map((card) => (
            <li key={card.id} style={{ marginBottom: '15px', border: '1px solid #ccc', borderRadius: '8px', padding: '10px' }}>
              <h2>{card.title}</h2>
              <p><b>Автор:</b> {card.author}</p>
              <p><b>Тип:</b> {card.type}</p>
              <p><b>Статус:</b> {card.status}</p>
              {card.reason && <p><b>Причина:</b> {card.reason}</p>}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
