import React, { useState, useEffect } from 'react';

function App() {
  const [user, setUser] = useState(null); // хранит залогиненого пользователя
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(false);

  // Загрузка карточек
  const fetchCards = () => {
    setLoading(true);
    fetch('http://localhost:3002/cards')
      .then(res => res.json())
      .then(data => {
        setCards(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  };

  // Имитация логина (у тебя должен быть реальный вход)
  const login = (login, password) => {
    // Тут твой запрос на бек с логином
    // Допустим, если всё ок:
    setUser({ login }); // просто пример, в реале токен, юзер-данные
    fetchCards();
  };

  // Имитация логаута
  const logout = () => {
    setUser(null);
    setCards([]);
  };

  if (!user) {
    return (
      <div>
        <h2>Вход</h2>
        {/* Простая кнопка для имитации */}
        <button onClick={() => login('ivan123', 'qwerty')}>Войти как ivan123</button>
      </div>
    );
  }

  return (
    <div>
      <h1>Добро пожаловать, {user.login}!</h1>
      <button onClick={logout}>Выйти</button>

      <h2>Карточки книг</h2>
      {loading ? <p>Загрузка...</p> : cards.length === 0 ? <p>Карточек нет :(</p> : (
        <ul>
          {cards.map(c => (
            <li key={c.id}>
              <b>{c.title}</b> — {c.author} ({c.type})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
